levelSlides.forEach((slide, index) => {
 slide.addEventListener('click', () => {
  currentLevel = levels[index - 2];
  levelTasks = currentLevel.tasks;
  renderLevel(currentLevel);

  renderTasks(currentLevel, levelTasks);
 });
});

renderTasks(currentLevel, levelTasks);

